import React from "react";
import { Link } from "react-router-dom";

const papers = [
  {
    title: "Heavily augmented sound event detection utilizing weak predictions",
    authors: "H. Nam et al.",
    venue: "DCASE Challenge Tech. rep., 2021",
    link: "https://dcase.community/documents/challenge2021/technical_reports/DCASE2021_Nam_6.pdf"
  },
  {
    title: "FilterAugment: An Acoustic Environmental Data Augmentation Method",
    authors: "H. Nam et al.",
    venue: "ICASSP, 2022",
    link: "https://ieeexplore.ieee.org/document/9747455"
  },
  {
    title: "JiTTER: Jigsaw Temporal Transformer for Event Reconstruction for Self-Supervised Sound Event Detection",
    authors: "H. Nam and Y.-H. Park",
    venue: "TASLP, to be published, 2025",
    link: "https://arxiv.org/abs/2502.20857"
  },
];

export default function Research() {
  return (
    <div className="px-6 py-12 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold tracking-tight mb-8 text-center uppercase">Publications</h2>
      <div className="space-y-4">
        {papers.map((paper, index) => (
          <div key={index} className="border rounded-xl p-4 hover:shadow-md transition bg-white/70 backdrop-blur-sm">
            <a href={paper.link} target="_blank" rel="noopener noreferrer" className="text-base font-medium text-indigo-600 hover:underline">
              {paper.title}
            </a>
            <p className="text-sm text-gray-600">{paper.authors}</p>
            <p className="text-sm text-gray-700">{paper.venue}</p>
          </div>
        ))}
      </div>
      <div className="mt-10 text-center">
        <Link to="/" className="text-sm text-gray-500 hover:text-indigo-600 underline">‚Üê Back to Home</Link>
      </div>
    </div>
  );
}
