import React from "react";

export default function About() {
  return (
    <div className="px-6 py-12 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold tracking-tight mb-8 text-center uppercase">About</h2>
      <blockquote className="italic text-xl text-gray-800 text-center mb-6">
        "I seek to understand the world and myself through research, music, and craftsmanship."
      </blockquote>
      <p className="text-gray-700 text-md leading-relaxed">
        I'm Hyeonuk Nam, an auditory intelligence researcher, singer-songwriter, and handmade guitar builder. I strive to find harmony between logical analysis and creative expression. My work aims to connect the structure of sound with human perception, and I believe the most meaningful life comes from deeply engaging with the present moment through diverse forms of creation.
      </p>
    </div>
  );
}
