// About.tsx - Dark Theme Matching
import React from "react";
import { Link } from "react-router-dom";

export default function About() {
  return (
    <div className="p-6 max-w-3xl mx-auto text-gray-100 bg-gray-900 min-h-screen">
      <h2 className="text-4xl font-extrabold mb-6 text-indigo-300">About Me</h2>
      <p className="text-lg text-gray-300 mb-8">
        I'm Hyeonuk Nam – a PhD candidate at KAIST in Mechanical Engineering, focusing on Auditory Intelligence.
        I’m also an indie rock musician and a passionate hand-crafted guitar builder.
      </p>
      <Link to="/" className="text-indigo-400 hover:underline text-sm">← Back to Home</Link>
    </div>
  );
}
